import React from 'react';
import { Button, Form } from 'react-bootstrap';

const UserSearch = ({ searchedMember, setSearchedMember, userList, searchUsers}) => {
	const [searchValue, setSearchValue] = useState('');
  	const [filteredUsers, setFilteredUsers] = useState([]);

  	useEffect(() => {
	    setFilteredUsers( userList.filter(user => user.toLowerCase().includes(searchValue.toLowerCase())) );
  	}, [searchValue, userList]);

  	const handleSearch = (event) => {
	    const value = event.target.value;
	    setSearchTerm(value);
	    setSearchedMember(value);
	};

	return (
		<div>
	      <Form.Control
	        type="text"
	        placeholder="Search users..."
	        value={searchValue}
	        onChange={handleSearch}
	      />
	      {filteredUsers && (
	        <ListGroup className="mt-2 position-absolute">
	          {filteredUsers.map((user, index) => (
	            <ListGroup.Item
	              key={index}
	              action
	              onClick={() => {
	                setSearchTerm(user);
	                setSearchedMember(user);
	              }}
	            >
	              {user}
	            </ListGroup.Item>
	          ))}
	        </ListGroup>
	      )}
	    </div>
  	);
};

export default UserSearch;
