import React from 'react';

const MessageField = ({ messages = [] }) => {
  return (
    <div className="MessageField">
      <ul>
        {messages.map((message, index) => ( // show all messages 
          <li key={index}>
            <strong>{message.username}</strong> ({new Date(message.timestamp).toLocaleString()}): {message.content}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default MessageField;
