import React, { useState, useEffect } from 'react';
import LoginForm from './LoginForm';
import MessageInput from './MessageInput';
import MessageField from './MessageField';
import UserSearch from './UserSearch';
import NavigationHead from './NavigationHead';
import io from 'socket.io-client';


const socket = io('http://localhost:8000');

function MainWindow() {

  // main functional
  const [username, setUsername] = useState(''); // current username
  const [channelName, setChannelName] = useState(''); // current chat
  const [message, setMessage] = useState(''); // message from input
  const [messages, setMessages] = useState([]); // state of messages from chat
  const [userList, setUserList] = useState([]); // users
  const [searchedMember, setSearchedMember] = useState(''); //searched member of chat
  const [loginForm, setLoginForm] = useState(true);

  useEffect(() => { // actions after rendering
    socket.on('join-user', (user) => { // sub on event from server 'search-users-result'
      setUserList((prevUserList) => [...prevUserList, user]); // add user in the end of users list
    });

    socket.on('receive-message', (data) => { // sub on event from server 'receive-message'
      console.log("messages");
      setMessages((prevMessages) => [...prevMessages, `${data.username} (${data.timestamp}): ${data.content}`]);
      console.log(messages);
    });

    socket.on('update-members', (members) => {
      setUserList(members); // update members list
    });

    socket.on('update-message', (messages) => {
      setMessages(messages); // update message
    });
    return () => { // cansel all subs 
      socket.off('join-user');
      socket.off('update-members');
      socket.off('receive-message');
      socket.off('update-message');
    };
  }, []);

  const joinChannel = () => {
    if (username && channelName) {
      socket.emit('join-channel', { channel_name: channelName, username: username });
      setLoginForm(false); // hide login form
    }
  };

  const sendMessage = () => {
    if (message.trim() && channelName) { // clear spaces around the message
      const data = {
        content: message,
        channel_name: channelName,
        username,
      };
      socket.emit('send-message', data); // send 'send-message' event to server
      setMessage(''); // сlear input after sending
    }
  };

  const searchUsers = () => {
    if (searchedMember) {
      socket.emit('search-users', searchedMember);
    }
  };
  
  return (
    <>
      {loginForm && (
        <LoginForm
          username={username}
          setUsername={setUsername}
          setChannelName={setChannelName}
          channelName={channelName}
          joinChannel={joinChannel}
        />
      )}
      {!loginForm && (
        <>
          <NavigationHead
            searchedMember={searchedMember}
            setSearchedMember={setSearchedMember}
            userList={userList}
            searchUsers={searchUsers}
          />
          <MessageField messages={messages} />
          <MessageInput message={message} setMessage={setMessage} sendMessage={sendMessage} />
        </>
      )}
    </>
  );
}

export default MainWindow;
