import React, { useState, useEffect } from 'react';
import { Button, Form, ListGroup } from 'react-bootstrap';

const UserSearch = ({ setSearchedMember, userList, setUsername }) => {
	const [searchValue, setSearchValue] = useState('');
  	const [filteredUsers, setFilteredUsers] = useState([]);
  	const [viewHints, setViewHints] = useState(false);

  	useEffect(() => {
	    setFilteredUsers( userList.filter(user => user?.username?.toLowerCase().includes(searchValue.toLowerCase())) );
  	}, [searchValue, userList]);

  	const handleSearch = (event) => {
	    const value = event.target.value;
	    setSearchValue(value);
	    setSearchedMember(value);
	    setViewHints(true);
	};

	return (
		<div>
	      <Form.Control
	      	className="d-flex"
	        type="text"
	        placeholder="Search users..."
	        value={searchValue}
	        onChange={handleSearch}
	        style={{width: "500px"}}
	      />
	      {filteredUsers.length && viewHints && (
	        <ListGroup className="mt-1 position-absolute" style={{width: "500px"}}>
	          {filteredUsers.map((user, index) => (
	            <ListGroup.Item
	              key={index}
	              action
	              onClick={() => {
	                setSearchValue(user.username);
	                setUsername(user.username);
	              }}
	            >
	              {user.username}
	            </ListGroup.Item>
	          ))}
	        </ListGroup>
	      )}
	    </div>
  	);
};

export default UserSearch;
