import React, { useState, useEffect } from 'react';
import LoginForm from './LoginForm';
import MessageInput from './MessageInput';
import MessageList from './MessageField';
import UserSearch from './UserSearch';
import io from 'socket.io-client';

const socket = io('http://localhost:8000');

function MainWindow() {

  // main functional
  const [username, setUsername] = useState(''); // current username
  const [channelName, setChannelName] = useState(''); // current chat
  const [message, setMessage] = useState(''); // message from input
  const [messages, setMessages] = useState([]); // state of messages from chat
  const [userList, setUserList] = useState([]); // users
  const [searchedMember, setSearchedMember] = useState(''); //searched member of chat

  useEffect(() => { // actions after rendering
    socket.on('search-users-result', (users) => { // sub on event from server 'search-users-result'
      setUserList(users);
    });
    socket.on('receive-message', (message) => { // sub on event from server 'receive-message'
      setMessages((prevMessages) => [...prevMessages, message]);
    });
    return () => {
      socket.off('receive-message');
      socket.off('search-users-result');
    };
  }, []);
  const joinChannel = () => {
    socket.emit('join-channel', channelName, username); // send 'join-channel' event to server
  };

  const sendMessage = () => {
    socket.emit('send-message', channelName, message); // send 'send-message' event to server
    setMessage('');
  };

  const searchUsers = () => {
    socket.emit('search-users', searchedMember);  // send 'search-users' event to server
  };
  
  return (
    <>
      <LoginForm
        username={username}
        setUsername= {setUsername}
        setChannelName ={setChannelName}
        channelName = {channelName}
        joinChannel={joinChannel}
      />
      <UserSearch
        searchedMember={searchedMember}
        setSearchedMember = {setSearchedMember}
        userList={userList}
        searchUsers={searchUsers}
      />
      <MessageField messages = {messages} />
      <MessageInput message={message} setMessage={setMessage} sendMessage={sendMessage} />
    </>
  );
}
