import React from 'react';
import { Button, Form } from 'react-bootstrap';
import { MDBCard, MDBContainer, MDBCardBody, MDBInput } from 'mdb-react-ui-kit';

const LoginForm = ({ username, setUsername, channelName, setChannelName, joinChannel }) => {
  return (
    <MDBContainer fluid className='d-flex align-items-center justify-content-center bg-image'>
      <MDBCard className='m-4' style={{maxWidth: '800px'}}>
        <MDBCardBody className='px-5'>
          <h2 className="text-center mb-5">Login to chat</h2>
          <Form.Group className="mb-3">
            <Form.Label>Username</Form.Label>
            <MDBInput // simple authification without password
              type="text"
              value ={username}
              label="Enter username..."
              className='w-100'
              onChange = {(event) => setUsername(event.target.value)} // when change input stage change temporary value
            />
          </Form.Group>
          <Form.Group className="mb-3">
            <Form.Label>Channel</Form.Label>
            <MDBInput // input channel name
              type = "text"
              label ="Enter channel name..."
              value = {channelName} 
              className='w-100'
              onChange = {(event) => setChannelName(event.target.value)} // when change input stage change temporary value 
            />
          </Form.Group>
          <Button className="button" onClick = {joinChannel}>Join</Button>
        </MDBCardBody>
      </MDBCard>
    </MDBContainer>
  );
};

export default LoginForm;
